
var swiper = new Swiper(".clients_swiper", {
    slidesPerView: "auto",
    spaceBetween: 30,
    loop: true,
    navigation: {
        nextEl: ".slidenext",
        prevEl: ".slideprev",
    },
});
var menu = 0;
$(".burgermenu").click(function(){
    if(menu==0){
        $(".header_cont ul").css("width", "100%");
        menu = 1
    }else{
        $(".header_cont ul").css("width", "0");
        menu = 0
    }
})
$(".scrolllink").click(function(){
    $('html, body').animate({
        scrollTop:$("."+$(this).data("src")+"").position().top
    }, 500);
});